package javaday1;

public class Day2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Increment and decrement Operator
		
		// Assigning the value first and then increasing
		
		int d = 30;
		int k = d++;
		System.out.println("the value of d ="+d);
		System.out.println("the value of k="+k);
		
		// Assigning the operator first and and then value
		
		int y = 30;
		int u = ++y;
		
		System.out.println("the value of u="+u);
		System.out.println("the value of y="+y);
		
	// IF Condition
		
		int h = 63;
		if(h==63) {
			System.out.println("condition is true");
			
	  // Else Condition
		
			int j = 12;
			
			if(j<=12) {
				System.out.println("condition is true");
			} else {
				
				System.out.println("condition is false");
			}
			
		// If Else If statement
			
			int english =99;
			int urdu = 80;
			int science = 78;
			
			int obtained = english+urdu+science;
			int avg = obtained/3;
			
			System.out.println(avg);
			
			if (avg>=90 && avg<=100) {
				System.out.println("the grade should be = A");
			} else if (avg >=80 && avg <=89){
				System.out.println("the grade should be =B");
			} else if (avg >=70 && avg<=79){
				
				System.out.println("the grade should be = C");
			} else {
				
				System.out.println("try it again");
			}
		
			// Nested If Statement
			
			
		}	
				
			
			
		}
		
}
	





	
	

