package javaday1;

public class Day3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// SWITCH STATEMENTS..We have to change the vale in "string day" line..the purpose
		
		String Day = "1";
		switch(Day) {
		
		case "1" :
			System.out.println("Today is Monday");
			break;
		case "2":
			System.out.println("today is tuesday");
			break;
		case "3":
			System.out.println("today is wednesday");
			break;
		case "4":
			System.out.println("today is thursday");
			break;
		case "5":
			System.out.println("today is friday");
			break;
		case "6":
			System.out.println("today is saturday");
			break;
		case "7":
			System.out.println("today is sunday");
			break;
		
		}
		
		// WHILE LOOPS//
		
		int j=1;
		while(j<=100) {
			System.out.println(j);
			j++;
		}
		
	// Print the number from 1 to 100 in descending order
		
		int l=100;
		while(l>=1) {
			System.out.println(l);
			l--;
			
		}
		
	// DO WHILE LOOPS//
		int h=1;
		do {
			System.out.println(h);
			h++;
		}while(h<=10);
		
	// FOR LOOP
		
		for(int y =1;y<=20;y++) {
			System.out.println(y);
		}
		
   // ARRAYS ( SYNTAX OF ARRAY IS)
		//datatype arraysign name of array=new size of array
		
		
		
		
		
		}
}
	



        

       
        
         
